mapper.select:
select user_id
from assignments
where assignment_id = #{assignmentId};

select *
from assignments
where user_id = #{userId}
  and course_id = #{courseId};

mapper.update:
update assignments
set credit=#{credit},statu = 1
where assignment_id = #{assignmentId};

update assignments
set assignment_url=#{assignmentUrl}
where assignment_id = #{assignmentId};

mapper.select:
select a.assignment_id, u.username, a.assignment_url, a.course_id, a.time
from assignments a
         join user u on a.user_id = u.user_id
where course_id = #{courseId}
  and a.statu = 0;

select a.assignment_id, u.username, a.assignment_url, a.course_id, a.credit, a.time
from assignments a
         join user u on a.user_id = u.user_id
where course_id = #{courseId}
  and a.statu = 1;
