mapper.select:
select user_id
from assignments
where assignment_id = #{assignmentId};

select *
from assignments
where user_id = #{userId}
  and course_id = #{courseId};

mapper.update:
update assignments
set credit=#{credit}
    and statu = 1
where assignment_id = #{assignmentId};

mapper.select:
select a.assignment_id, u.username, a.assignment_url, a.course_id, a.time
from assignments a
         join user u on a.user_id = u.user_id
where course_id = #{courseId}
  and statu = 0;

select a.assignment_id, u.username, a.assignment_url, a.course_id, a.credit, a.time
from assignments a
         join user u on a.user_id = u.user_id
where course_id = #{courseId}
  and statu = 1;

select c.course_id, c.name, count(a.assignment_id) as ungraded_assignments_count
from course c
         left join assignments a on c.course_id = a.course_id and c.user_id = a.user_id
where c.user_id = #{userId}
  and a.statu = 0
group by c.course_id, c.name;
