mapper.select:
select course_id
from course_audit
where user_id = #{userId}
  and name = #{name}
  and statu IN (1, 2);

select *
from course_audit
where user_id = #{userId}
  and statu = 2
  and checked = 0;

select *
from course_audit
where user_id = #{userId}
  and statu = 2
  and checked = 2;

mapper.update:
update course_audit
set statu=0
where course_id = #{courseId};
