mapper.select:
select course_id
from course
where user_id = #{userId}
  and name = #{name}
  and statu = 1;

select *
from course
where user_id = #{userId}
  and statu = 1
  and checked = 1;

select *
from course
where user_id = #{userId}
  and statu = 1
  and checked = 0;

select *
from course
where user_id = #{userId}
  and statu = 1
  and checked = 2;

mapper.update:
update course
set statu=0
where name = #{name};
