mapper.select:
select course_id
from course
where user_id = #{userId}
  and name = #{name}
  and statu IN (1, 2);

select *
from course
where user_id = #{userId}
  and statu = 1
  and checked = 1;

select *
from course
where user_id = #{userId}
  and statu = 0;

mapper.update:
update course
set statu=0
where course_id = #{courseId};

mapper.select:
select course.*
from course
         inner join subject on course.sub_name = subject.sub_name
where subject.sub_category = #{category}
  and course.statu = 1;

select course.*
from course
         inner join subject on course.sub_name = subject.sub_name
where subject.sub_name = #{subName}
  and course.statu = 1;

select price
from course
where course_id = #{courseId};
