mapper.update:
update user
set phone=#{phone},
    email=#{email}
where username = #{username}

update user
set password=#{newPwd}
where email = #{email};

mapper.select:
select username, phone, email, statu
from user
where role = 2;

select user_id
from user
where phone = #{phone}

mapper.update:
update user
set statu= #{statu}
where username = #{username}

update user
set image_url=#{url}
where username = #{username};

mapper.select:
select credit
from user
where user_id = #{userId};

mapper.update:
update user
set credit=#{credit}
where user_id = #{userId};
