mapper.update:
update user
set phone=#{phone},
    email=#{email}
where username = #{username}

update user
set password=#{newPwd}
where username = #{username};

mapper.select:
select username, phone, email, statu
from user
where role = 2;

select username, phone, email, statu
from user
where role = 1;

select user_id
from user
where phone = #{phone}

select user_id
from user
where email = #{email}

mapper.update:
update user
set image_url=#{url}
where username = #{username};
